BEGIN TRANSACTION;
DROP TABLE IF EXISTS "fkl_verse";
CREATE TABLE IF NOT EXISTS "fkl_verse" (
	"id"	integer NOT NULL,
	"sura_id"	int(11) NOT NULL,
	"new_sura"	tinyint(4) DEFAULT NULL,
	"verse_no"	int(11) NOT NULL,
	"page"	int(11) DEFAULT NULL,
	"hizb_page"	int(11) DEFAULT NULL,
	"hizb"	int(11) NOT NULL,
	"juz"	int(11) NOT NULL,
	"new_juz"	tinyint(4) DEFAULT NULL,
	"verse"	varchar(2047) NOT NULL,
	"sajdah"	tinyint(1) NOT NULL DEFAULT '0',
	PRIMARY KEY("id" AUTOINCREMENT)
);
COMMIT;
